import React from 'react';
import { MenuIcon, BellIcon } from '../../icons';
import { useAuth } from '../../contexts/AuthContext';

const Header = ({ openSidebar }) => {
  const { currentUser, logout } = useAuth();

  return (
    <header className="sticky top-0 z-10 flex-shrink-0 flex h-16 bg-white shadow">
      <button
        type="button"
        className="px-4 border-r border-gray-200 text-gray-500 lg:hidden"
        onClick={openSidebar}
      >
        <span className="sr-only">Open sidebar</span>
        <MenuIcon className="h-6 w-6" />
      </button>
      <div className="flex-1 px-4 flex justify-between">
        <div className="flex-1 flex items-center">
          <h1 className="text-2xl font-semibold text-gray-900 lg:hidden">HMS</h1>
        </div>
        <div className="ml-4 flex items-center md:ml-6">
          {/* Notification button */}
          <button
            type="button"
            className="bg-white p-1 rounded-full text-gray-400 hover:text-gray-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            <span className="sr-only">View notifications</span>
            <BellIcon className="h-6 w-6" />
          </button>

          {/* Profile dropdown */}
          <div className="ml-3 relative">
            <div className="flex items-center">
              <div className="ml-3">
                <div className="text-base font-medium leading-none text-gray-700">
                  {currentUser?.name || 'User'}
                </div>
                <div className="text-sm font-medium leading-none text-gray-500 mt-1">
                  {currentUser?.email || 'user@example.com'}
                </div>
              </div>
              <button 
                onClick={logout}
                className="ml-3 bg-blue-100 px-3 py-1 rounded-md text-sm text-blue-800 hover:bg-blue-200"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
